import React from 'react';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.css';
import $ from "jquery";

export const MatrimonialCreateProfile = () => {
    $(document).ready( function() {
    	$(document).on('change', '.btn-file :file', function() {
		var input = $(this),
			label = input.val().replace(/\\/g, '/').replace(/.*\//, '');
		input.trigger('fileselect', [label]);
		});

		$('.btn-file :file').on('fileselect', function(event, label) {
		    
		    var input = $(this).parents('.input-group').find(':text'),
		        log = label;
		    
		    if( input.length ) {
		        input.val(log);
		    } else {
		        if( log ) alert(log);
		    }
	    
		});
		function readURL(input) {
		    if (input.files && input.files[0]) {
		        var reader = new FileReader();
		        
		        reader.onload = function (e) {
		            $('#img-upload').attr('src', e.target.result);
		        }
		        
		        reader.readAsDataURL(input.files[0]);
		    }
		}

		$("#imgInp").change(function(){
		    readURL(this);
		}); 	
	});
return (
<>
<section className="masthead">
   <div className="container">
      <div className="row">
         <div className="col-md-12">
            <h5 className="mb-3"><Link to="/Matrimonial">
                Matrimonial </Link> &#62; Matrimonial Create Profile </h5>
         </div>
      </div>
      <form>
         <div className="group mt-4 mb-4">
            <div className="row mt-4">
               <div className="col-md-6 mb-4">
                  <label className="mb-1">Business Title </label>
                  <input type="text" className="form-control" placeholder="Business Title"/>
               </div>
               <div className="col-md-4 mb-4">
                  <div class="form-group">
        <label>Upload Image</label>
        <div class="input-group">
            <span class="input-group-btn">
                <span class="btn btn-default btn-file">
                  <input type="file" id="imgInp"/>
                </span>
            </span>
            <input type="text" class="form-control" readonly />
        </div>
        
    </div>
    <div className="col-md-4 mb-4">
    <img src='img' id='img-upload'/>
    </div>
               </div>
               <div className="col-md-6 mb-4">
                  <label className="mb-1">Business location</label>
                  <input type="text" className="form-control" placeholder="Country or state"/>
               </div>
               <div className="col-md-6 mb-4">
                  <label className="mb-1">Business type</label>
                  <select className="form-control" >
                     <option value="FULL_TIME">Full-time</option>
                     <option value="PART_TIME"> Part-time </option>
                     <option value="CONTRACT"> Contract </option>
                     <option value="TEMPORARY"> Temporary </option>
                     <option value="VOLUNTEER"> Volunteer </option>
                     <option value="INTERNSHIP"> Internship </option>
                  </select>
               </div>
               <div className="col-md-12 mb-4">
                  <label className="mb-1">Add a Business description</label>
                  <textarea class="form-control" rows="5" placeholder="Business responsibilities:"></textarea>
               </div>
               <div className="col-md-6 mb-4">
                  <Link to="/ApplySuccess" className="btn btn-primary">
                  Create New Business Post</Link>
               </div>
            </div>
         </div>
      </form>
   </div>
</section>
</>
)
}
export default MatrimonialCreateProfile;
