<section className="masthead">
   <div className="container">
      <div className="row">
         <div className="col-md-12">
            <h5 className="mb-3">
               <Link to="/Matrimonial">
               Matrimonial </Link> &#62; Matrimonial Create Profile 
            </h5>
         </div>
      </div>
      <form>
         <div className="group mt-4 mb-4">
            <div className="row mt-4">
            <div className="col-md-8 mb-4">
            <div className="row">
                    <div className="col-md-4 mb-4">
                    <label className="mb-1">Business Title </label>
                  <input type="text" className="form-control" placeholder="Business Title"/>
                    </div>
                    <div className="col-md-4 mb-4">
                    <label className="mb-1">Business Title </label>
                  <input type="text" className="form-control" placeholder="Business Title"/>
                  </div>
                  <div className="col-md-4 mb-4">
                  <label className="mb-1">Business Title </label>
                  <input type="text" className="form-control" placeholder="Business Title"/>
                  </div>
                  
                  </div>
                    </div>
                    <div className="col-md-4 mb-4">
                    <label className="mb-1">Business Title </label>
                  <input type="text" className="form-control" placeholder="Business Title"/>
                  </div>






               <div className="col-md-4 mb-4">
                  <label className="mb-1">Business Title </label>
                  <input type="text" className="form-control" placeholder="Business Title"/>
               </div>
               
               <div className="col-md-4 mb-4">
                    
                  </div>
               <div className="col-md-4 mb-4">
              
                  <div class="form-group">
                     
                     <img src='img' id='img-upload'/>
                     <div class="input-group">
                    
                        <span class="input-group-btn">
                        <label>Upload Image</label>
                        <span class="btn-file">
                        <input  className="form-control"  type="file" id="imgInp"/>
                        </span>
                        </span>
                        <input type="text" class="form-control d-none" readonly />
                     </div>
                  </div>
                  
               </div>
               <div className="col-md-6 mb-4">
                  <label className="mb-1">Business location</label>
                  <input type="text" className="form-control" placeholder="Country or state"/>
               </div>
               <div className="col-md-6 mb-4">
                  <label className="mb-1">Business type</label>
                  <select className="form-control" >
                     <option value="FULL_TIME">Full-time</option>
                     <option value="PART_TIME"> Part-time </option>
                     <option value="CONTRACT"> Contract </option>
                     <option value="TEMPORARY"> Temporary </option>
                     <option value="VOLUNTEER"> Volunteer </option>
                     <option value="INTERNSHIP"> Internship </option>
                  </select>
               </div>
               <div className="col-md-12 mb-4">
                  <label className="mb-1">Add a Business description</label>
                  <textarea class="form-control" rows="5" placeholder="Business responsibilities:"></textarea>
               </div>
               <div className="col-md-6 mb-4">
                  <Link to="/ApplySuccess" className="btn btn-primary">
                  Create New Business Post</Link>
               </div>
            </div>
         </div>
      </form>
   </div>
</section>
</>
)
}
