import React from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from "jquery";

export const DialPad = () => {
    /* DialPad by Anthony Bernard*/

let btns = document.getElementById("btns");
let showPicked = document.getElementById("showPicked");
let buttonNumbers = [
  [1, '<i class="material-icons" style="font-size: 11px">&#xe0d9;</i>'],
  [2, "ABC"],
  [3, "DEF"],
  [4, "GHI"],
  [5, "JKL"],
  [6, "MNO"],
  [7, "PQRS"],
  [8, "TUV"],
  [9, "WXYZ"],
  ["*", ""],
  [0, ""],
  ["#", ""]
];
let picked = [];
let deleteNums = document.getElementById("delete");
let call = document.getElementById("call");
let end = document.getElementById("end");

buttonNumbers.forEach(numbers => {
  btns.innerHTML += `
    <button onclick="addNumbers(this.value)" class="btn btn-light m-1" style="width:5rem; hieght:10rem;" type="button" value="${
      numbers[0]
    }">${numbers[0]}<p>${numbers[1]}</p></button>`;
});

function addNumbers(numbers) {
  picked.push(numbers);
  showPicked.innerHTML = picked.join("");
}

deleteNums.addEventListener("click", () => {
  picked.pop();
  showPicked.innerHTML = picked.join("");
  console.log(picked);
});

call.addEventListener("click", () => {
  showPicked.innerHTML = `Calling ${picked.join("")}`;
  picked = [];
});

end.addEventListener("click", () => {
  showPicked.innerHTML = "Call Ended";
  picked = [];
});

  return (
    <>
   <section className="masthead">
   <div className="container">
      <div className="row justify-content-center h-100">
         <div className="col-md-6">
            <div className="form-input-content">
               <div className="card">
                  <div className="card-body dialpad">
                 
<h3 id="showPicked" class="text-center mt-5"></h3>
<div class="container mt-2">
  <div class="row justify-content-center">
    <div class="phone" style="width: 20rem">
      <div class="container">
        <div id="btns" class="row justify-content-center mt-5"></div>
      </div>
      <div class="container mt-4 rounded">
        <div class="row">
          <div class="col text-center">
            <button id="call" class="btn btn-success mr-4">
                  <i class="fas fa-phone"></i>
                </button>
            <button id="end" class="btn btn-danger mr-4">End</button>
            <button id="delete" class="btn btn-light">
                  <i class="fas fa-backspace"></i>
                </button>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
                  </div>
               </div>
            </div>
         </div>
      </div>
   </div>
</section>
    </>
  )
}

export default DialPad;
