import React from 'react';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.css';
import $ from "jquery";
export const MatrimonialCreateProfile = () => {
$(document).ready( function() {
$(document).on('change', '.btn-file :file', function() {
var input = $(this),
label = input.val().replace(/\\/g, '/').replace(/.*\//, '');
input.trigger('fileselect', [label]);
});
$('.btn-file :file').on('fileselect', function(event, label) {
var input = $(this).parents('.input-group').find(':text'),
log = label;
if( input.length ) {
input.val(log);
} else {
if( log ) alert(log);
}
});
function readURL(input) {
if (input.files && input.files[0]) {
var reader = new FileReader();
reader.onload = function (e) {
$('#img-upload').attr('src', e.target.result);
}
reader.readAsDataURL(input.files[0]);
}
}
$("#imgInp").change(function(){
readURL(this);
}); 	
});
return (
<>
<section className="masthead">
   <div className="container">
      <div className="row">
         <div className="col-md-12">
            <h5 className="mb-3">
               <Link to="/Matrimonial">
               Matrimonial </Link> &#62; Matrimonial Create Profile 
            </h5>
         </div>
      </div>
      <form>
         <div className="group mt-4 mb-4">
            <div className="row mt-4">
               <div className="col-md-9">
                  <div className="row">
                     <div className="col-md-6 mb-4">
                        <label className="mb-1">Create Profile For</label>
                        <select className="form-control">
<option value="" selected="selected">Please Select</option>
<option value="1">Self</option>
<option value="2">Son</option>
<option value="2D">Daughter</option>
<option value="4">Relative/Friend</option>
<option value="6D">Sister</option>
<option value="6">Brother</option>
<option value="5">Client-Marriage Bureau</option>
</select>
                     </div>
                     
                     <div className="col-md-6 mb-4">
                        <label className="mb-1">Date of Birth </label>
                        <input type="date"  className="form-control" />
                     </div>
                     <div className="col-md-6 mb-4">
                        <label className="mb-1">Business Title </label>
                        <input type="text" className="form-control" placeholder="Business Title"/>
                     </div>
                     <div className="col-md-6 mb-4">
                        <label className="mb-1">Business Title </label>
                        <input type="text" className="form-control" placeholder="Business Title"/>
                     </div>
                     <div className="col-md-6 mb-4">
                        <label className="mb-1">Enter Your Contact Number </label>
                        <input type="text" className="form-control" placeholder="123 4567 890"/>
                     </div>
                     <div className="col-md-6 mb-4">
                        <label className="mb-1">Enter Your Email </label>
                        <input type="text" className="form-control" placeholder="example@email.com"/>
                     </div>
                     
                  </div>
               </div>
               <div className="col-md-3 mb-4">
               <label className="mb-1">Upload Profile Pic</label>
                  <div className="form-group">
                     <img src='img/male-female.png' id='img-upload' className="img-thumbnail mb-2"/>
                     <div className="input-group">
                        <span className="input-group-btn">
                        
                        <span className="btn-file">
                        <input  className="form-control"  type="file" id="imgInp"/>
                        </span>
                        </span>
                        <input type="text" className="form-control d-none" readonly />
                     </div>
                  </div>
               </div>
               <div className="col-md-12 mb-4">
                  <label className="mb-1">Add a Business description</label>
                  <textarea class="form-control" rows="5" placeholder="Business responsibilities:"></textarea>
               </div>
               <div className="col-md-12 text-center  mb-4">
                  <Link to="/ApplySuccess" className="btn btn-primary">
                  Create Your Profile</Link>
               </div>
            </div>
         </div>
      </form>
   </div>
</section>
</>
)
}
export default MatrimonialCreateProfile;
